```scala
import scala.util.{Failure, Success, Try}
import com.google.maps.{GeoApiContext, GeocodingApi}

object GeocodeHelper {
  val context = new GeoApiContext().setApiKey("YOUR_API_KEY")

  def getGeocodeResponse(lat: Double, lng: Double): Option[String] = {
    val resultTry = Try(GeocodingApi.reverseGeocode(context, new com.google.maps.model.LatLng(lat, lng)).await())
    resultTry match {
      case Success(result) =>
        if (result != null && result.length > 0) {
          Some(result(0).formattedAddress)
        } else {
          None
        }
      case Failure(_) =>
        None
    }
  }
}
```
