class UserNetworkManager: ObservableObject {
    private let cloudKitService = CloudKitService(containerIdentifier: "com.yourcompany.yourapp")
    private let apiManager = APIManager()

    @Published var users: [User] = []
    @Published var isLoading = false

    func fetchUsers() {
        isLoading = true

        // 从 CloudKit 中获取用户
        cloudKitService.fetchUsers { records, error in
            DispatchQueue.main.async {
                self.isLoading = false
            }

            if let error = error {
                print(error.localizedDescription)
                return
            }

            guard let records = records else {
                print("No records found")
                return
            }

            let users = records.map { User.fromCKRecord($0) }

            // 将从 CloudKit 中获取到的用户与从 API 中获取到的用户进行合并
            self.users = self.mergeUsers(self.users, users)
        }

        // 从 API 中获取用户
        apiManager.fetchUsers { users, error in
            DispatchQueue.main.async {
                self.isLoading = false
            }

            if let error = error {
                print(error.localizedDescription)
                return
            }

            // 将从 CloudKit 中获取到的用户与从 API 中获取到的用户进行合并
            self.users = self.mergeUsers(self.users, users)
        }
    }

    private func mergeUsers(_ existingUsers: [User], _ newUsers: [User]) -> [User] {
        var users = existingUsers

        for user in newUsers {
            if let index = users.firstIndex(where: { $0.id == user.id }) {
                users[index] = user
            } else {
                users.append(user)
            }
        }

        return users
    }
}
